sys_script_include.script:
var Transformtest = Class.create();
Transformtest.prototype = {
    initialize: function() {},

    run: function(transformMapId, importSetId) {
        //var transformMap1Id = 'cfeddf9b83da521089f6c8a6feaad3de';
        //var transformMap2Id = '9eae5fdb83da521089f6c8a6feaad389';

        var importSetGr = new GlideRecord('sys_import_set');
        importSetGr.addQuery('sys_id', importSetId);
        importSetGr.setLimit(1);
        importSetGr.query();
        if (importSetGr.next()) {
            var transformer = new GlideImportSetTransformer();
            transformer.setMapID(transformMapId);
            transformer.transformAllMaps(importSetGr);
        }
    },

    type: 'Transformtest'
};
